INSERT INTO PUBLIC.PRODUCTS (NAME, DESCRIPTION, PRICE, PRICE_UNIT, CATEGORY_ID, IMAGE_PATH) VALUES ('Samsung n110', 'A television set, more commonly called a television, TV, TV set, television receiver, or telly, is a device that combines a tuner, display, and loudspeakers for the purpose of viewing television. Introduced in the late 1920s in mechanical form, television sets became a popular consumer product after World War II in electronic form, using cathode ray tubes. The addition of color to broadcast television after 1953 further increased the popularity of television sets in the 1960s, and an outdoor antenna became a common feature of suburban homes.', 299.50, 'USD', 5, '/ec9d44660f0d4b1c957cd3f7cb9e723a.jpg');
